import {call, all, delay, fork, put, takeLatest} from "redux-saga/effects";
import axios from 'axios';
import {    LOG_IN_FAILURE, LOG_IN_REQUEST, LOG_IN_SUCCESS,
    LOG_OUT_FAILURE, LOG_OUT_REQUEST, LOG_OUT_SUCCESS } from '../reducers/user';

function logInAPI(data){
    return axios.post('/api/login', data)
}

function* logIn(action){
    try{
        // const result = yield call(logInAPI, action.data)
        yield delay(1000);
        yield put({
            type: LOG_IN_SUCCESS,
            data: action.data
        });
    } catch (e) {
        yield put({
            type: LOG_IN_FAILURE,
            error: e.response.data,
        })
    }
}

// 로그인아웃 API
function logOutAPI(){
    return axios.post('/api/logout')
}

function* logOut(){
    try{
        const result = yield call(logOutAPI)
        yield put({
            type: LOG_OUT_SUCCESS,
            data: result.data
        });
    } catch (e) {
        yield put({
            type: LOG_OUT_FAILURE,
            error: e.response.data,
        })
    }
}

//signUp api는 제너레이터가아니다
function signUpAPI(){
    return axios.post('/api/logout')
}

function* signUp(){
    try{
        const result = yield call(signUpAPI)
        yield put({
            type: LOG_OUT_SUCCESS,
            data: result.data
        });
    } catch (e) {
        yield put({
            type: LOG_OUT_FAILURE,
            error: e.response.data,
        })
    }
}

function* watchLogin(){
    //takeLatest 두번눌렸을때 마지막꺼만
    yield takeLatest(LOG_IN_REQUEST, logIn);
}
function* watchLogout(){
    yield takeLatest(LOG_OUT_REQUEST, logOut);
}
function* watchSignUp(){
    yield takeLatest(SIGN_UP_REQUEST, signUp);
}

export default function* userSaga(){
    yield all([
        //fork는 함수를 실행해준다
        fork(watchLogin),
        fork(watchLogout),
        fork(watchSignUp),
    ])
}
