import React, { useState } from 'react';
// npm i prop-tyeps ì„¤ì¹˜ë¥¼ í•´ì¤˜ì•¼ì§€ ëŒì•„ê°„ë‹¤
import PropTypes from 'prop-types';
import Link from 'next/link';
// ë””ìì¸
import { Menu, Input, Row, Col, Button } from 'antd';
import 'antd/dist/antd.css'; // or 'antd/dist/antd.less'

import styled from 'styled-components';
import { useSelector } from 'react-redux';
import UserProfile from './UserProfile';
import LoginForm from './LoginForm';

// ìŠ¤íƒ€ì¼ ì»´í¬ë„ŒíŠ¸
const SearchInput = styled(Input.Search)`
  vertical-align: middle;

`;

const AdminLayout = ({ children }) => {
  const { isLoggedIn } = useSelector((state) => state.user);
  return (
    <div>
      <Menu
        mode="horizontal"
        style={{
          display: 'flex',
          justifyContent: 'space-between',
          // maxWidth: '1000px',
          margin: '0 auto',
          color: '#FFFFFF' }}
      >
        <Menu.Item>
          <Link href="/">
            <a style={{ fontSize: '1.2rem' }}>
              StudyBaord
            </a>
          </Link>
        </Menu.Item>
        <Menu.Item>
          <Link href="/profile"><a>ğŸ˜€ Profile </a></Link>
        </Menu.Item>
        <Menu.Item>
          <Link href="/studyBoard"><a>â“ Ask Question</a></Link>
        </Menu.Item>
        <Menu.Item>
          <Link href="/about"><a>ğŸ“¢ About </a></Link>
        </Menu.Item>
        <Menu.Item>
          <Link href="/rank"><a>ğŸ‘ Rank </a></Link>
        </Menu.Item>
        <Menu.Item>
          <SearchInput
            enterButton
            placeholder="type what you want"
            allowClear
            style={{
              width: 400,
              color: '#FFFFFF',
              borderRadius: '20px' }}
          />
        </Menu.Item>
        <Menu.Item />
      </Menu>
      {/* ê±°í„°ë¼ëŠ”ê±´ ì»¬ëŸ¼ì‚¬ì´ ê°„ê²© */}
      <Row gutter={8}>
        <Col xs={24} md={6}>
          <div style={{ width: '70%', margin: '30px auto' }}>
            <div style={{ paddingTop: '10px', paddingBottom: '10px' }}>
              <Link href="/user"><Button block style={{ borderRadius: '8px' }}><a>User</a></Button></Link>
            </div>
            <div style={{ paddingTop: '10px', paddingBottom: '10px' }}>
              <Link href="/reportedPost"><Button block style={{ borderRadius: '8px' }}><a>Reported Post</a></Button></Link>
            </div>
            {/* <div style={{ paddingTop: '10px', paddingBottom: '10px' }}> */}
            {/*  <Link href="/contact"><Button block style={{ borderRadius: '8px' }}><a>Contact</a></Button></Link> */}
            {/* </div> */}
          </div>
        </Col>
        <Col xs={24} md={16}>
          { children }
        </Col>

      </Row>
    </div>
  );
};

AdminLayout.propTypes = {
  // ì¹ ë“œëŸ°ì€ ë…¸ë“œì…ë‹ˆë‹¤ í™”ë©´ì— ê·¸ë¦´ ìˆ˜ ìˆëŠ”ê²Œ ë…¸ë“œ
  children: PropTypes.node.isRequired,
};

export default AdminLayout;
