import React from 'react';
// npm i prop-tyeps 설치를 해줘야지 돌아간다
import PropTypes from 'prop-types';
import Link from 'next/link';
//디자인 row , col 반응형
import { Menu, Input, Row, Col } from 'antd';
import 'antd/dist/antd.css'; // or 'antd/dist/antd.less'

const AppLayout = ({ children }) => {
    return(
        <div>
            <Menu mode="horizontal">
                <Menu.Item>
                    <Link href="/"><a>IGC트윗 📢</a></Link>
                </Menu.Item>
                <Menu.Item>
                    <Link href="/profile"><a>프로필 </a></Link>
                </Menu.Item>
                <Menu.Item>
                    {/*스타일을 줘야함 */}
                    <Input.Search enterButton style={{ verticalAlign: 'middle' }}/>
                </Menu.Item>
                <Menu.Item>
                    <Link href="/signup"><a>회원가입 </a></Link>
                </Menu.Item>
            </Menu>
            {/*컬럽 사이에 간격 gutter*/}
            <Row gutter={8}>
                {/*24개 컬럼 화면 100퍼센트 다사용 6개를 사용 25퍼센트 */}
                <Col xs={24} md={6} >
                    로그인
                </Col>
                <Col xs={24} md={12} >
                    { children }
                </Col>
                <Col xs={24} md={6} >
                    투두리스트
                    오른쪽메뉴
                    <a href="http://www.cubelink.me" target="_black" rel="noreferrer noopenr">Made by PKHong</a>
                </Col>
            </Row>

        </div>
    );
};

AppLayout.propTypes = {
    // 칠드런은 노드입니다 화면에 그릴 수 있는게 노드
    children: PropTypes.node.isRequired,
};

export default AppLayout;


// 다른 레이아웃을 만들고싶으면 다른 레이아웃 파일을 만들면된다
// 예 otherlayout 만들면 Layout 으로 적요해서 감싸면된다
