import React from 'react';
// npm i prop-tyeps 설치를 해줘야지 돌아간다
import PropTypes from 'prop-types';
import Link from 'next/link';

import { Menu, Input, Row, Col } from 'antd';


const AppLayout = ({ children }) => {
    return(
        <div>
            <Menu mode="horizontal">
                <Menu.Item>
                    <Link href="/"><a>Studyboard 📢</a></Link>
                </Menu.Item>
                <Menu.Item>
                    <Link href="/profile"><a>Profile </a></Link>
                </Menu.Item>
                <Menu.Item>
                    <Link href="/about"><a>About </a></Link>
                </Menu.Item>
                <Menu.Item>
                    {/*스타일을 줘야함 */}
                    <Input.Search placeholder="Type it what you looking for" enterButton style={{ verticalAlign: 'middle', color: '#ffb618' }}/>
                </Menu.Item>
                <Menu.Item>
                    <Link href="/signup"><a>SignUp </a></Link>
                </Menu.Item>
            </Menu>

            {/*컬럽 사이에 간격 gutter*/}
            <Row gutter={10}>
                {/*24개 컬럼 화면 100퍼센트 다사용 6개를 사용 25퍼센트 */}
                <Col xs={24} md={6} >
                    로그인
                </Col>
                <Col xs={24} md={12} >
                    { children }
                </Col>
                <Col xs={24} md={6} >
                    오른쪽 메뉴

                </Col>
            </Row>
        </div>
    );
};

AppLayout.propTypes = {
    // 칠드런은 노드입니다 화면에 그릴 수 있는게 노드
    children: PropTypes.node.isRequired,
};

export default AppLayout;
