import React from 'react';
import Head from 'next/head';
import { Row, Col, Layout, Result, Button, Table, Tag, Space } from 'antd';
import { SmileOutlined } from '@ant-design/icons';
import AppLayout from '../components/AppLayout';
import AdminLayout from '../components/AdminLayout';

const { Header, Footer, Content } = Layout;

const columns = [
  {
    title: 'Name',
    dataIndex: 'name',
    key: 'name',
    render: (text) => <a>{text}</a>,
  },
  {
    title: 'Content',
    dataIndex: 'Content',
    key: 'Content',
  },
  {
    title: 'Tags',
    key: 'tags',
    dataIndex: 'tags',
    render: (tags) => (
      <>
        {tags.map((tag) => {
          let color = tag.length > 5 ? 'geekblue' : 'green';
          if (tag === 'loser') {
            color = 'volcano';
          }
          return (
            <Tag color={color} key={tag}>
              {tag.toUpperCase()}
            </Tag>
          );
        })}
      </>
    ),
  },
  {
    title: 'Action',
    key: 'action',
    render: (text, record) => (
      <Space size="middle">
        <a>Invite {record.name}</a>
        <a>Delete</a>
      </Space>
    ),
  },
];

const data = [
  {
    key: '1',
    name: 'John Brown',
    age: 32,
    Content: 'New York No. 1 Lake Park',
    tags: ['nice', 'developer'],
  },
  {
    key: '2',
    name: 'Jim Green',
    age: 42,
    Content: 'London No. 1 Lake Park',
    tags: ['loser'],
  },
  {
    key: '3',
    name: 'Joe Black',
    age: 32,
    Content: 'Sidney No. 1 Lake Park',
    tags: ['cool', 'teacher'],
  },
];

const Rank = () => (
  <>
    <Head>
      <title>Study Board | Admin</title>
    </Head>

    <AdminLayout>

      <div style={{ padding: '0.5rem 1rem' }}>

        <div>
          <Table columns={columns} dataSource={data} />
        </div>
      </div>

    </AdminLayout>
  </>
);

export default Rank;
