import React, {useCallback, useState} from 'react';
import { Form, Input, Checkbox, Button } from 'antd';
import Head from "next/head";
import styled from 'styled-components';
import AppLayout from '../components/AppLayout';
import useInput from '../hooks/useinput';

const ErrorMessage =styled.div`
  color: red;
`;

const Signup = () => {
    const [id, onChangeId] = useInput('');
    const [nickname, onChangNickname] = useInput('');
    const [password, onChangPassword] = useInput('');

    const [passwordCheck, setPasswordCheck] = useState('');
    const [passwordError, setPasswordError] = useState(false);
    const onChangPasswordCheck = useCallback((e) => {
        setPasswordCheck(e.target.value);
        setPasswordError(e.target.value !== password);
    },[]);

    const onSubmit = useCallback(() => {
        if (password !== passwordCheck){
            return setPasswordError(true);
        }
        if (!term){
            return setTermError(true);
        }
        console.log(id,nickname,password);
    },[password,passwordCheck,term]);

    const [term, setTerm] = useState('');
    const [termError,setTermError] = useState(false);
    const onChangeTerm = useCallback((e) => {
        setTerm(e.target.checked);
        setTermError(false);
    },[]);

    return(
        <>
            <AppLayout>
                <Head>
                    <title>Study Board | Profile</title>
                </Head>
                <Form onFinish={onSubmit}>
                    <div>
                        <label htmlFor="user-id">ID</label>
                        <br/>
                        <Input name="user-id" value={id} required onChange={onChangeId} />
                    </div>
                    <div>
                        <label htmlFor="user-nick">NickName</label>
                        <br/>
                        <Input name="user-nick" value={nickname} required onChange={onChangNickname} />
                    </div>
                    <div>
                        <label htmlFor="user-password">Password</label>
                        <br/>
                        <Input name="user-password" type="password" value={password} required onChange={onChangPassword} />
                    </div>
                    <div>
                        <label htmlFor="user-password-check">Password (again)</label>
                        <br/>
                        <Input
                            name="user-password-check"
                            type="password"
                            value={passwordCheck}
                            required
                            onChange={onChangPasswordCheck}
                        />
                        {passwordError && <ErrorMessage style={{color: 'red'}}>Password is not match</ErrorMessage>}
                    </div>
                    <div>
                        <Checkbox name="user-term" checked={term} onChange={onChangeTerm}>You mush agree this</Checkbox>
                        {termError && <ErrorMessage style={{ color: 'red'}}>You need check the box.</ErrorMessage>}
                    </div>
                    <div style={{ marginTop: 10}}>
                        <Button type="primary" htmlType="submit">Sign In</Button>
                    </div>
                </Form>
            </AppLayout>
        </>
    );
}

export default Signup;
