import React, { useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import Link from 'next/link';
import { Button, Menu, Input, Row, Col } from 'antd';
import PostForm from '../components/PostForm';
import PostCard from '../components/PostCard';
import AppLayout from '../components/AppLayout';
import { LOAD_POSTS_REQUEST } from '../reducers/post';

const studyBoard = () => {
  const dispatch = useDispatch();
  const { me } = useSelector((state) => state.user);
  const { mainPosts, hasMorePosts, loadPostsLoading } = useSelector((state) => state.post);

  useEffect(() => {
    dispatch({
      type: LOAD_POSTS_REQUEST,
    });
  }, []);

  useEffect(() => {
    // scrolly : 얼마나 내렸는지 , clientHeight: 화면 보이는 길이, scrollHeight: 총길이
    // react-vertualized 구현 해봐야함
    function onScroll() {
      if (window.scrollY + document.documentElement.clientHeight > document.documentElement.scrollHeight - 500) {
        if (hasMorePosts && !loadPostsLoading) {
          dispatch({
            type: LOAD_POSTS_REQUEST,
            data: mainPosts[mainPosts.length - 1].id,
          });
        }
      }
    }
    window.addEventListener('scroll', onScroll);
    return () => {
      window.removeEventListener('scroll', onScroll);
    };
  }, [mainPosts, hasMorePosts, loadPostsLoading]);

  return (
    <AppLayout>

      {me && <PostForm />}
      <Row>

        <Col span={6}>
          <Link href="/"><Button block style={{ borderWidth: 'thin', borderRadius: '20px', color: 'rgb(29,29,31)', textDecoration: 'none' }}><a> Recently </a></Button></Link>
        </Col>
        <Col span={6}>
          <Link href="/"><Button block><a style={{ borderWidth: 'thin', borderRadius: '20px', color: 'rgb(29,29,31)', textDecoration: 'none' }}> Popular </a></Button></Link>
        </Col>

      </Row>
      {mainPosts.map((p) => (
        <PostCard key={p.id} post={p} />
      ))}

    </AppLayout>
  );
};

export default studyBoard;
