import {HYDRATE} from "next-redux-wrapper";
//리듀서 합쳐주는거

import user from './user';
import post from './post';
//초기상태
const initialState = {
    user:{
    }
    ,
    post:{

    }
};

//비동기 엑션 크리에이터

//action creator example
// const changeNickname = (data) => {
//     return {
//         type: 'CHANGE_NICKNAME',
//         data,
//     }
// };

//(이전상태, 액션) => 다음상태
const rootReducer = (state = initialState, action) => {
    switch (action.type) {
        //
        case HYDRATE:
            return { ...state, ...action.payload};
        case 'LOG_IN':
            return{
                ...state,
                user: {
                    ...state.user,
                    isLoggedIN: true,
                    user: action.data,
                }
            };
        case 'LOG_OUT':
            return{
                ...state,
                user: {
                    ...state.user,
                    isLoggedIN: false,
                    user: null,
                }
            };
        default:
            return state;
    }
};

export default rootReducer;
